import { useReducer } from 'react'
import { TodoAdd } from './TodoAdd'
import { TodoList } from './TodoList'
import { todoReducer } from './todoReducer'

const initialState  = [
    {
        id : new Date().getTime(),
        description: 'Recolectar la piedar del alma',
        done: false,
    },
    {
        id : new Date().getTime() *3,
        description: 'Recolectar la piedar del infinito',
        done: false,
    }
]

export const TodoApp = () => {

    //si solo hay un reducer se pude dejar solo dispatch, si tengo mas de uno le coloco un nombre representartivo todoDispacth
const [todos, dispatch] = useReducer( todoReducer, initialState)


  return (
    <>
        <h1>Todo App 10, <small>pendientes: 2</small></h1>
        <hr />

        <div className='row'>
            <div className='col-7'>
                <TodoList  todos={todos} />
            </div>
                <div className='col-5'>
                <h4>Agregar TODO</h4>
                <hr />
                <TodoAdd />
            </div>
        </div>

     </>

  )
}
