import { useEffect } from "react";


export const Message = () => {

  useEffect(() => {

    //creo el listener 
    const onMouseMove = ({x, y})=>{
      const coords = {x, y};
      console.log(coords);
    }

    window.addEventListener('mousemove', onMouseMove)

    return () => {

    window.removeEventListener('mousemove', onMouseMove)
    }
  }, [])


  return (
    <>
        <h3>Usuario ya existe</h3>

    </>
  )
}
