import { useForm } from '../hooks'

export const TodoAdd = () => {

    const {formState, setFormState} = useForm({
        id : new Date().getTime(),
        description: '',
        done: false,

    })


    const {id, description, done} = formState; 

    const onNewTodo = ({target})=>{
        const {description, value} = target;
        setFormState({
            ...formState,
            [ description ]: value
        })
    }

  return (
    <form>
        <input
            type='text'
            placeholder = '¿Qué hay que hacer?'
            className='form-control'
            name='description'
            value={description}
            onChange={onNewTodo}
        />
        <button 
            type='submit'
            className='btn btn-outline-primary mt-1'
        >Agregar
        </button>
    </form>
  )
}
